const input = $input.first().json.body;
const errors = [];

if (!input.topic || input.topic.length < 10 || input.topic.length > 200) {
  errors.push({ field: "topic", message: "Must be 10-200 chars", received: input.topic });
}
if (input.topic && !/^[a-zA-Z0-9äöüÄÖÜß\s\-_,.!?]+$/.test(input.topic)) {
  errors.push({ field: "topic", message: "Contains invalid characters", received: input.topic });
}

const validStyles = ["cinematic", "documentary", "educational"];
const style = input.style || "cinematic";
if (!validStyles.includes(style)) {
  errors.push({ field: "style", message: "Must be: " + validStyles.join(", "), received: style });
}

const duration = parseInt(input.target_duration) || 60;
if (duration < 30 || duration > 180) {
  errors.push({ field: "target_duration", message: "Must be 30-180", received: duration });
}

if (errors.length > 0) {
  return { json: { valid: false, errors } };
}

return {
  json: {
    valid: true,
    validated_data: {
      topic: input.topic.trim(),
      style,
      target_duration: duration
    },
    execution_id: $execution.id
  }
};

const responseText = $input.first().json.content[0].text;
let parsed;

try {
  parsed = JSON.parse(responseText);
} catch (e) {
  const match = responseText.match(/```json?\s*([\s\S]*?)```/);
  if (match) {
    parsed = JSON.parse(match[1]);
  } else {
    throw new Error("Claude returned invalid JSON: " + responseText.substring(0, 200));
  }
}

if (!parsed.script || !parsed.scenes || parsed.scenes.length === 0) {
  throw new Error("Claude response missing required fields");
}

return { json: parsed };

const input = $input.first();
const binaryData = input.binary?.data;

if (!binaryData) {
  throw new Error('No audio data found');
}

function parseFileSizeToBytes(fileSize) {
  if (typeof fileSize === 'number') return fileSize;
  if (typeof fileSize !== 'string') return 0;

  const normalized = fileSize.trim().replace(',', '.');
  const match = normalized.match(/(\d+\.?\d*)\s*(B|KB|MB|GB)?/i);
  if (!match) return 0;

  const value = parseFloat(match[1]);
  const unit = (match[2] || 'B').toUpperCase();
  const multipliers = { B: 1, KB: 1024, MB: 1024 ** 2, GB: 1024 ** 3 };
  return value * (multipliers[unit] || 1);
}

function estimateBitrateFromMp3Header(buffer) {
  if (!buffer || buffer.length < 4) return null;

  let offset = 0;
  if (buffer.slice(0, 3).toString('ascii') === 'ID3' && buffer.length > 10) {
    const size = ((buffer[6] & 0x7f) << 21) | ((buffer[7] & 0x7f) << 14) | ((buffer[8] & 0x7f) << 7) | (buffer[9] & 0x7f);
    offset = 10 + size;
  }

  const bitratesMpeg1L3 = [0, 32, 40, 48, 56, 64, 80, 96, 112, 128, 160, 192, 224, 256, 320, 0];
  const bitratesMpeg2L3 = [0, 8, 16, 24, 32, 40, 48, 56, 64, 80, 96, 112, 128, 144, 160, 0];

  for (let i = offset; i < buffer.length - 4; i++) {
    const b1 = buffer[i];
    const b2 = buffer[i + 1];
    if (b1 !== 0xff || (b2 & 0xe0) !== 0xe0) continue;

    const versionBits = (b2 >> 3) & 0x03;
    const layerBits = (b2 >> 1) & 0x03;
    if (layerBits !== 0x01) continue;

    const b3 = buffer[i + 2];
    const bitrateIndex = (b3 >> 4) & 0x0f;

    if (versionBits === 0x03) return bitratesMpeg1L3[bitrateIndex] || null;
    if (versionBits === 0x02 || versionBits === 0x00) return bitratesMpeg2L3[bitrateIndex] || null;
  }

  return null;
}

let audioBytes = parseFileSizeToBytes(binaryData.fileSize);
let buffer = null;

if (typeof binaryData.data === 'string') {
  try {
    buffer = Buffer.from(binaryData.data, 'base64');
    if (!audioBytes) audioBytes = buffer.length;
  } catch (error) {
    console.log('Could not decode base64 audio buffer:', error.message);
  }
}

if (!audioBytes || audioBytes <= 0) {
  throw new Error('Could not determine audio size for duration estimation');
}

const bitrateKbps = estimateBitrateFromMp3Header(buffer) || 128;
const estimatedDuration = audioBytes / ((bitrateKbps * 1000) / 8);

const executionId = $('Input Validation').first().json.execution_id;

return {
  json: {
    audio_duration: Math.round(estimatedDuration * 10) / 10,
    alignment: null,
    normalized_alignment: null,
    timing_mode: 'estimated_from_size_and_header',
    execution_id: executionId
  },
  binary: {
    audio: binaryData
  }
};

// Audio Duration vom ElevenLabs Node holen
const audioDuration = $('ElevenLabs Timing Extraction').first().json.audio_duration;

// execution_id vom Supabase Upload Node holen
const executionId = $json.execution_id;

// Falls execution_id undefined ist, vom ElevenLabs Node holen
const finalExecutionId = executionId || $('ElevenLabs Timing Extraction').first().json.execution_id;

// Muss exakt mit der Upload-Domain übereinstimmen
const supabaseUrl = 'https://ywdwvjriklaevktswnwe.supabase.co';

const audioUrl = `${supabaseUrl}/storage/v1/object/public/audio/${finalExecutionId}.mp3`;

console.log('Audio Duration:', audioDuration);
console.log('Execution ID:', finalExecutionId);
console.log('Audio URL:', audioUrl);

return {
  json: {
    audio_url: audioUrl,
    audio_duration: audioDuration,
    execution_id: finalExecutionId
  }
};

const audioDuration = $('ElevenLabs Timing Extraction').first().json.audio_duration;
const scenes = $('Claude Response Parser').first().json.scenes;

const totalHintDuration = scenes.reduce((sum, s) => sum + s.duration_hint, 0);

const scenesWithDuration = scenes.map(scene => {
  const proportionalDuration = (scene.duration_hint / totalHintDuration) * audioDuration;
  const runwayDuration = Math.max(5, Math.min(Math.round(proportionalDuration), 10));
  return {
    ...scene,
    runway_duration: runwayDuration
  };
});

const totalVideoDuration = scenesWithDuration.reduce((sum, s) => sum + s.runway_duration, 0);
const durationGap = audioDuration - totalVideoDuration;

return {
  json: {
    scenes: scenesWithDuration,
    audio_duration: audioDuration,
    total_video_duration: totalVideoDuration,
    duration_gap: durationGap,
    needs_fallback: durationGap > 2
  }
};

const previousData = $input.first().json;
const taskId = previousData.image_task_id ?? previousData.id;

if (!taskId) {
  throw new Error('Missing Runway image task id on polling input.');
}

const maxPollMs = 8 * 60 * 1000;
const pollStartedAt = previousData.image_poll_started_at ?? Date.now();
const elapsedMs = Date.now() - pollStartedAt;

if (elapsedMs > maxPollMs) {
  throw new Error(`Runway image task ${taskId} timeout after ${Math.round(elapsedMs / 1000)}s`);
}

const sceneContext = previousData.scenes
  ?? $('Scene Loop').first().json.scenes
  ?? $('Split Out').first().json.scenes
  ?? null;

const response = await this.helpers.httpRequest({
  method: 'GET',
  url: `https://api.dev.runwayml.com/v1/tasks/${taskId}`,
  headers: {
    'Authorization': 'Bearer key_454b9d472e9e3332f2ce2e237b8efba36871d460c9d517d0bc8dd342b13a1680b4682140d24d31648975b314460126e1d2fc5fc1b426b59b2e39f0d682d4ad4c',
    'X-Runway-Version': '2024-11-06'
  },
  json: true
});

const status = response.status ?? 'UNKNOWN';

if (status === 'FAILED' || status === 'CANCELLED') {
  throw new Error(`Runway image task ${taskId} ended with status ${status}`);
}

const result = {
  ...previousData,
  scenes: sceneContext,
  image_task_id: taskId,
  runway_image_status: status,
  image_poll_started_at: pollStartedAt,
  image_poll_attempts: (previousData.image_poll_attempts ?? 0) + 1
};

if (status === 'SUCCEEDED') {
  const output = response.output?.[0] ?? null;

  if (!output) {
    throw new Error(`Runway image task ${taskId} succeeded but did not return an output URL`);
  }

  result.image_url = output;
}

return { json: result };

const previousData = $input.first().json;
const taskId = previousData.video_task_id ?? previousData.id;

if (!taskId) {
  throw new Error('Missing Runway video task id on polling input.');
}

const maxPollMs = 14 * 60 * 1000;
const pollStartedAt = previousData.video_poll_started_at ?? Date.now();
const elapsedMs = Date.now() - pollStartedAt;

if (elapsedMs > maxPollMs) {
  throw new Error(`Runway video task ${taskId} timeout after ${Math.round(elapsedMs / 1000)}s`);
}

const sceneContext = previousData.scenes
  ?? $('Image Ready?').first().json.scenes
  ?? $('Scene Loop').first().json.scenes
  ?? $('Split Out').first().json.scenes
  ?? null;

const imageContext = previousData.image_url
  ?? $('Image Ready?').first().json.image_url
  ?? null;

const imageTaskContext = previousData.image_task_id
  ?? $('Image Ready?').first().json.image_task_id
  ?? null;

const task = await this.helpers.httpRequest({
  method: 'GET',
  url: `https://api.dev.runwayml.com/v1/tasks/${taskId}`,
  headers: {
    'Authorization': 'Bearer key_454b9d472e9e3332f2ce2e237b8efba36871d460c9d517d0bc8dd342b13a1680b4682140d24d31648975b314460126e1d2fc5fc1b426b59b2e39f0d682d4ad4c',
    'X-Runway-Version': '2024-11-06'
  },
  json: true
});

const status = task.status ?? 'UNKNOWN';

if (status === 'FAILED' || status === 'CANCELLED') {
  throw new Error(`Runway video task ${taskId} ended with status ${status}`);
}

const result = {
  ...previousData,
  scenes: sceneContext,
  image_url: imageContext,
  image_task_id: imageTaskContext,
  video_task_id: taskId,
  runway_video_status: status,
  video_poll_started_at: pollStartedAt,
  video_poll_attempts: (previousData.video_poll_attempts ?? 0) + 1
};

if (status === 'SUCCEEDED') {
  const output = task.output?.[0] ?? null;

  if (!output) {
    throw new Error(`Runway video task ${taskId} succeeded but did not return an output URL`);
  }

  result.video_url = output;
  result.status = 'SUCCEEDED';
}

return { json: result };

const scenes = $input.all().map((item, index) => ({
  scene_id: index + 1,
  video_url: item.json.video_url,
  video_task_id: item.json.video_task_id,
  image_url: item.json.image_url,
  image_task_id: item.json.image_task_id
}));

const audioDuration = $('ElevenLabs Timing Extraction').first().json.audio_duration;
const audioUrl = $('Build Audio URL').first().json.audio_url;
const originalScenes = $('Calculate Scene Durations').first().json.scenes;

return {
  json: {
    scenes: scenes.map((s, i) => ({
      ...s,
      ...originalScenes[i],
      actual_video_duration: originalScenes[i].runway_duration
    })),
    audio_duration: audioDuration,
    audio_url: audioUrl
  }
};

// Build Creatomate Payload (komplett ersetzen)  ✅
// FIX: Scene timing/duration auf die richtigen Layer setzen (Scene-X-Video / Scene-X-Text)
// metadata bleibt EXAKT wie bei dir (STRING)

const scenes = $json.scenes;
const audioDuration = $json.audio_duration;
const audioUrl = $json.audio_url;

if (!Array.isArray(scenes) || scenes.length === 0) {
  throw new Error("No scenes found in $json.scenes");
}
if (!audioUrl) {
  throw new Error("No audio_url found in $json.audio_url");
}
if (typeof audioDuration !== "number" || Number.isNaN(audioDuration)) {
  throw new Error("audio_duration is missing or not a number");
}

const modifications = {
  "Audio-Track": audioUrl,
  "Audio-Track.duration": audioDuration,
};

let cumulativeTime = 0;

// GENUTZTE SCENES (mit Runway-Videos füllen)
scenes.forEach((scene, index) => {
  const sceneNum = index + 1;

  if (!scene.video_url) {
    throw new Error(`Scene ${sceneNum} missing video_url`);
  }

  const dur = Number(scene.actual_video_duration ?? scene.runway_duration ?? scene.duration_hint ?? 5);
  const sceneDuration = Math.max(0, Math.min(10, Math.round(dur))); // 0..10 sinnvoll (Runway max 10)

  const videoKey = `Scene-${sceneNum}-Video`;
  const textKey = `Scene-${sceneNum}-Text`;

  // Video-URL von Runway einfügen
  modifications[`${videoKey}.source`] = scene.video_url;

  // ✅ FIX: Timing/Dauer auf Video-Layer setzen (nicht Scene-X.*)
  modifications[`${videoKey}.time`] = cumulativeTime;
  modifications[`${videoKey}.duration`] = sceneDuration;

  // Text-Overlay (falls vorhanden)
  if (scene.text_overlay) {
    const start = Number(scene.overlay_timing?.start ?? 1);
    const end = Number(scene.overlay_timing?.end ?? (sceneDuration - 1));

    const safeStart = Math.max(1, Math.min(sceneDuration - 1, start));
    const safeEnd = Math.max(safeStart + 0.5, Math.min(sceneDuration - 1, end));

    modifications[`${textKey}.text`] = String(scene.text_overlay);

    // ✅ FIX: Timing/Dauer auf Text-Layer setzen
    modifications[`${textKey}.time`] = cumulativeTime + safeStart;
    modifications[`${textKey}.duration`] = Math.max(0.5, safeEnd - safeStart);
  } else {
    modifications[`${textKey}.text`] = "";
    // optional: Text wirklich ausblenden
    modifications[`${textKey}.duration`] = 0;
  }

  cumulativeTime += sceneDuration;
});

// UNGENUTZTE SCENES (ausblenden durch duration = 0)
const totalScenes = 12; // Dein Template hat 12 Scenes
const usedScenes = scenes.length;

for (let i = usedScenes + 1; i <= totalScenes; i++) {
  const videoKey = `Scene-${i}-Video`;
  const textKey = `Scene-${i}-Text`;

  // ✅ FIX: auf Layer ausblenden (nicht Scene-i.*)
  modifications[`${videoKey}.source`] = "";
  modifications[`${videoKey}.duration`] = 0;

  modifications[`${textKey}.text`] = "";
  modifications[`${textKey}.duration`] = 0;
}

// execution_id
const executionId =
  $("Input Validation")?.first()?.json?.execution_id ||
  $execution.id ||
  "unknown";

// WICHTIG: Creatomate erwartet metadata als STRING
const metadata = JSON.stringify({ execution_id: executionId });

return {
  json: {
    template_id: "75b2838d-bbdf-42ee-86b1-507e37c85760",
    modifications,
    webhook_url: $execution.resumeUrl,
    metadata, // <-- string!
  },
};
